import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);
const store = new Vuex.Store({
  state: {
    listClothes: [
      { id: 1, name: "t-shirt", cost: 100, isDiscount: false },
      { id: 2, name: "dress", cost: 2000, isDiscount: true },
      { id: 3, name: "shorts", cost: 150, isDiscount: true },
    ],
  },
  actions: {},
  mutations: {},
  getters: {},
});

export default store;
